import React from "react";
import { Skill } from "../../../apis/model";
import "./skills.css";

interface Props {
  skills: Skill[];
}

const Skills: React.FC<Props> = ({ skills }) => {
  return (
    <div className="skills">
      <h2>Skills</h2>
      <ul className="list">
        {skills
          .sort((a, b) => b.level - a.level)
          .map((skill, index) => {
            return <SkillItem {...{ skill }} key={index} />;
          })}
      </ul>
    </div>
  );
};
export default Skills;

const SkillItem = ({ skill }: { skill: Skill }) => {
  return (
    <li className="list-item skill-item " style={{ opacity: skill.level }}>
      <p className="skill-text">{skill.type}</p>
    </li>
  );
};
