import React, { useEffect, useState } from "react";
import { fetchInfo } from "../apis/firebaseApi";
import { margin, Profile } from "../apis/model";
import Header from "./components/Header";
import Interests from "./components/Interests";
import List from "./components/List";
import { Spacer } from "./sharedComponents";

interface Props {}

const App: React.FC<Props> = () => {
  const [profileData, setProfileDate] = useState<Profile | null>(null);

  //Fetch profile data
  useEffect(() => {
    fetchInfo().then((result) => setProfileDate({ ...result }));
  }, []);

  /* //Analytics
  const [firebaseDocId, setFirebaseDocId] = useState("");
  const [viewedPage, setViewedPage] = useState("");
  useEffect(() => {
    if (profileData) {
      const windowDimensions = {
        height: window.innerHeight,
        width: window.innerWidth,
      };
      fetchGeo().then((res) => {
        if (res) {
          const { location } = res;
          const startDate = new Date();
          addAnalytics(location, windowDimensions, startDate, setFirebaseDocId);
        }
      });
    }
  }, [profileData]);

  useEffect(() => {
    if (viewedPage !== "" && firebaseDocId !== "") {
      addContactAnalytics(firebaseDocId, viewedPage);
    }
  }, [viewedPage, firebaseDocId]); */

  if (!profileData) return <></>;
  else {
    const { maschineLangs, humanLangs, skills, interests, mission } =
      profileData;

    /*   const humanLanguages = languages
      .filter((e) => e.human === true)
      .sort((a, b) => a.priority - b.priority)
      .map((e) => e.lang);

    const maschineLanguages = languages
      .filter((e) => e.human === false)
      .sort((a, b) => a.priority - b.priority)
      .map((e) => e.lang);
 */

    const sortedHumanLangs = humanLangs
      .sort((a, b) => b.priority - a.priority)
      .map((e) => e.lang);

    const sortedMaschineLangs = maschineLangs
      .sort((a, b) => b.priority - a.priority)
      .map((e) => e.lang);

    const specialities = skills.map((e) => e.type);

    return (
      <div className="page">
        <Header {...{ mission }} />

        <Spacer height={margin * 2} />

        <h2>Communication</h2>
        <h3>with humans</h3>
        <List listData={sortedHumanLangs} />
        <h3>with maschines</h3>
        <List listData={sortedMaschineLangs} />

        <Spacer height={margin * 2} />

        <h2>Specialities</h2>
        <List listData={specialities} />

        {/*     <Skills {...{ skills }} /> */}

        <Spacer height={margin * 2} />

        {/*  <Timeline {...{ timeline }} />

        <SeperatorItem seperator={sortedSeperators[3]} missing={"top"} /> */}

        <Interests {...{ interests }} />

        <Spacer height={margin * 2} />

        {/*   <Contact {...{ profileData }} /> */}


        <footer style={{ textAlign: "center" }}>
          <p className="">© Daniel Friedebold</p>
          {/* <p className="">Created with ❤️ in Rotterdam</p> */}
        </footer>
      </div>
    );
  }
};
export default App;
