import React from 'react'
import { generateStringSvg } from './svgGenerator'


interface Props {
  startYear: number
  stringSvgWidth: number
  svgHeight: number
  yearHeight: number
  markingWidth: number
}

const StringSvg: React.FC<Props> = ({ startYear, stringSvgWidth, svgHeight, yearHeight, markingWidth }) => {
  const generatedStrings = generateStringSvg(startYear)

  return (
    <div className='string-svg-container'>
      <svg height={svgHeight} width={stringSvgWidth} >
        {
          generatedStrings.map((year, index) => {
            return <text textAnchor='end'
              alignmentBaseline='middle'
              x={stringSvgWidth}
              fontSize='18px'
              y={index * yearHeight + markingWidth / 2}
              key={index}
            >{year}</text>
          })
        }
        <text textAnchor="end"
          alignmentBaseline='middle'
          x={stringSvgWidth}
          fontSize='18px'
          y={yearHeight * (new Date().getFullYear() - startYear + (new Date().getMonth()) / 12) + markingWidth / 2}
        >today</text>
      </svg>
    </div>
  )
}

export default StringSvg
