import React, { useEffect, useState } from "react";
import {
  addAnalytics,
  addContactAnalytics,
  fetchInfo
} from "./apis/firebaseApi";
import { fetchGeo } from "./apis/geoApi";
import { Profile } from "./apis/model";
import Contact from "./components/Contact";
import Header from "./components/Header";
import Interests from "./components/Interests";
import Languages from "./components/Languages";
import Skills from "./components/Skills";
import Timeline from "./components/Timeline";
import SeperatorItem from "./SeperatorItem";

interface Props {}

const Page: React.FC<Props> = () => {
  const [profileData, setProfileDate] = useState<Profile | null>(null);

  //Fetch profile data
  useEffect(() => {
    fetchInfo().then((result) => setProfileDate({ ...result }));
  }, []);

  //Analytics
  const [firebaseDocId, setFirebaseDocId] = useState("");
  const [viewedPage, setViewedPage] = useState("");
  useEffect(() => {
    if (profileData) {
      const windowDimensions = {
        height: window.innerHeight,
        width: window.innerWidth,
      };
      fetchGeo().then((res) => {
        if (res) {
          const { location } = res;
          const startDate = new Date();
          addAnalytics(location, windowDimensions, startDate, setFirebaseDocId);
        }
      });
    }
  }, [profileData]);

  useEffect(() => {
    if (viewedPage !== "" && firebaseDocId !== "") {
      addContactAnalytics(firebaseDocId, viewedPage);
    }
  }, [viewedPage, firebaseDocId]);

  if (!profileData) return <></>;
  else {
    const {
      languages,
      skills,
      interests,
      name,
      mission,
      timeline,
      seperators,
    } = profileData;
    const sortedSeperators = seperators.sort((a, b) => a.priority - b.priority);
    return (
      <div className="page">
       {/*  <div className="gradient-container">
          <div className="gradient-background" />
        </div> */}
        <Header {...{ name }} {...{ mission }} />

        <SeperatorItem seperator={sortedSeperators[0]} />

        <Languages {...{ languages }} />

        <SeperatorItem seperator={sortedSeperators[1]} />

        <Skills {...{ skills }} />

        <SeperatorItem seperator={sortedSeperators[2]} missing={"bottom"} />

        <Timeline {...{ timeline }} />

        <SeperatorItem seperator={sortedSeperators[3]} missing={"top"} />

        <Interests {...{ interests }} />

        <SeperatorItem seperator={sortedSeperators[4]} />

        <Contact {...{ profileData }} {...{ setViewedPage }} />

        <div style={{ height: "40px" }} />
        <div style={{ height: "40px" }} />

        <footer style={{ textAlign: "center" }}>
          <p className="">Made by Daniel Friedebold</p>
          <p className="">Created with ❤️ in Rotterdam</p>
        </footer>
      </div>
    );
  }
};
export default Page;
