import React, { useEffect, useState } from 'react';
import { sendEmail } from '../../apis/firebaseApi';
import './contact.css';

interface Props {
}

const ContactForm: React.FC<Props> = ({ }) => {

  const [email, setEmail] = useState('')
  const [name, setName] = useState<string>('')
  const [message, setMessage] = useState('')
  const [emailStatus, setEmailStatus] = useState<'non-initiated' | 'error' | 'success'>('non-initiated')
  const [showErrorMsg, setShowErrorMsg] = useState<boolean>(false)

  const dataEntered = email !== '' && name !== '' && message !== ''

  const removeErrorMsg = () => {
    if (showErrorMsg) setShowErrorMsg(false)
  }

  useEffect(() => {
    if (dataEntered) removeErrorMsg()
  }, [dataEntered])

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (dataEntered) {
      sendEmail(email, name, message, setEmailStatus)
    } else setShowErrorMsg(true)
  }

  return (
    <form className="contact-form" onSubmit={(e) => onSubmit(e)}>
      <input type="text" placeholder='Name' value={name} onChange={(e) => setName(e.target.value)} />
      <input type="email" placeholder='Email' aria-describedby="emailHelp" value={email} onChange={(e) => setEmail(e.target.value)} />
      <div className="form-txt-area">
        <textarea placeholder='Enter here your inquiry, some feedback, or just a story about your cat. ' value={message} onChange={(e) => setMessage(e.target.value)} />
      </div>
      {
        showErrorMsg &&
        <div className='contact-form-error-msg-container'>
          <p className='contact-form-error-msg'>Please fill in all Fields.</p>
        </div>
      }
      <button type="submit" className="contact-form-btn">Submit</button>
      {
        emailStatus === 'success' &&
        <div className='contact-form-submitted'>
          <h3 className='black'>Success!</h3>
          <p className='black'>I will get back to you asap.</p>
        </div>
      }
      {
        emailStatus === 'error' &&
        <div className='contact-form-submitted red'>
          <h3 className='black'>Something wend wrong</h3>
          <p className='black'>Please try to reach me through the links above.</p>
        </div>
      }

    </form>
  )
}
export default ContactForm;
