import React, { useEffect, useState } from "react";
import { fetchData } from "../apis/firebaseApi";
import { margin, Profile } from "../apis/model";
import Contact from "../other.../Contact";
import Dropdown from "./components/Dropdown";
import Header from "./components/Header";
import List from "./components/List";
import { Spacer } from "./sharedComponents";

interface Props {}

const App: React.FC<Props> = () => {
  const [profileData, setProfileDate] = useState<Profile | null>(null);
  const [openDropdown, setOpenDropdown] = useState<string>("");

  //Fetch profile data
  useEffect(() => {
    fetchData().then((data) => {
      if (data) setProfileDate({ ...data });
    });
  }, []);

  /* //Analytics
  const [firebaseDocId, setFirebaseDocId] = useState("");
  const [viewedPage, setViewedPage] = useState("");
  useEffect(() => {
    if (profileData) {
      const windowDimensions = {
        height: window.innerHeight,
        width: window.innerWidth,
      };
      fetchGeo().then((res) => {
        if (res) {
          const { location } = res;
          const startDate = new Date();
          addAnalytics(location, windowDimensions, startDate, setFirebaseDocId);
        }
      });
    }
  }, [profileData]);

  useEffect(() => {
    if (viewedPage !== "" && firebaseDocId !== "") {
      addContactAnalytics(firebaseDocId, viewedPage);
    }
  }, [viewedPage, firebaseDocId]); */

  if (!profileData) return <></>;
  else {
    const { maschineLangs, humanLangs, skills, interests, mission, timeline } =
      profileData;

    /*   const humanLanguages = languages
      .filter((e) => e.human === true)
      .sort((a, b) => a.priority - b.priority)
      .map((e) => e.lang);

    const maschineLanguages = languages
      .filter((e) => e.human === false)
      .sort((a, b) => a.priority - b.priority)
      .map((e) => e.lang);
 */

    const sortedHumanLangs = humanLangs
      .sort((a, b) => b.priority - a.priority)
      .map((e) => e.lang);

    const sortedMaschineLangs = maschineLangs
      .sort((a, b) => b.priority - a.priority)
      .map((e) => e.lang);

    const specialities = skills.map((e) => e.type);

    const sortedInterests = interests
      .sort((a, b) => a.priority - b.priority)
      .map((e) => e.type);

    const formalEducation = timeline.slice(0, 4);
    /* addToDB(formalEducation); */
    console.log(formalEducation);

    return (
      <div className="page">
        <Header {...{ mission }} />

        <Spacer height={margin * 2} />

        <Dropdown
          title="🗣 Communication Ability"
          content={
            <>
              <h3>with humans</h3>
              <List listData={sortedHumanLangs} />
              <Spacer height={margin / 2} />
              <h3>with maschines</h3>
              <List listData={sortedMaschineLangs} />
            </>
          }
          {...{ openDropdown }}
          {...{ setOpenDropdown }}
        />

        <Spacer height={margin} />

        <Dropdown
          title="💪🏽 Specialities"
          content={<List listData={specialities} />}
          {...{ openDropdown }}
          {...{ setOpenDropdown }}
        />

        <Spacer height={margin} />

        <Dropdown
          title="📚 Formal Education"
          content={<></>}
          {...{ openDropdown }}
          {...{ setOpenDropdown }}
        />
        <Spacer height={margin} />
        <Dropdown
          title="👨🏼‍💻 Work Experience"
          content={<></>}
          {...{ openDropdown }}
          {...{ setOpenDropdown }}
        />
        <Spacer height={margin} />
        <Dropdown
          title="🧪 Projects"
          content={<></>}
          {...{ openDropdown }}
          {...{ setOpenDropdown }}
        />

        <Spacer height={margin} />

        <Dropdown
          title="💭 Interests"
          content={<List listData={sortedInterests} />}
          {...{ openDropdown }}
          {...{ setOpenDropdown }}
        />

        <Spacer height={margin * 2} />

        <Contact {...{ profileData }} />
      </div>
    );
  }
};
export default App;
